export var tree = {
  inserted: function inserted(el, binding) {
    var rst = '';
    console.log('标签出入');

    var compile = function compile(n, levels) {
      rst += "<ul class='role-list-item' style='padding-top:6px;line-height:14px'>\
      <li class='expanded'>\
      <a style='margin-left: 0;' class='a_inline isChild' nodeid='" + n.id + "' parentId=" + n.parentId + " preImg='" + (n.preImg || '') + "'  href='javascript:void(0)'>\
      <span class='fa fa-angle-right'></span>\
      <span class='fa fa-angle-down'></span>\
      <span class='main-role'>" + n.guideCate + "<span class='node_id'> #" + n.id + "</span></span>\
      </a>\
      <a v-if='" + n.isChooseAll + "' ng-click='deleteAll(\"" + levels + "\")' href='javascript: void(0)'>删除</a>\
      <a v-if='" + n.isChooseAll + "' ng-click='cancelAll(\"" + levels + "\")' href='javascript: void(0)'>取消</a>\
      <a v-if='" + !n.isChooseAll + "' ng-click='chooseAll(\"" + levels + "\")' href='javascript: void(0)'>多选</a>\
      <div class='children'>";

      var recursion = function recursion(n, levels) {
        if (n.nodes && n.nodes.length) {
          var nodes = n.nodes;

          for (var i = 0; i < nodes.length; i++) {
            var nextLevels = [levels, i].join(',');

            if (nodes[i].nodes && nodes[i].nodes.length) {
              rst += "<ul class='role-list-item' style='padding-top:6px;line-height:14px'>\
                    <li class='expanded'>\
                    <input ng-checked='" + nodes[i].isChecked + "' ng-click='chooseChange(\"" + nextLevels + "\")' ng-if='" + n.isChooseAll + "' type='checkbox'>\
                    <a style='margin-left: 0;' class='a_inline isChild' nodeid='" + nodes[i].id + "' preImg='" + (n.preImg || '') + "' href='javascript:void(0)'>\
                    <span class='fa fa-angle-right'></span>\
                    <span class='fa fa-angle-down'></span>\
                    <span class='main-role'>" + nodes[i].guideCate + "<span class='node_id'> #" + nodes[i].id + "</span></span>\
                    </a>\
                    <a v-if='" + nodes[i].isChooseAll + "' ng-click='deleteAll(\"" + nextLevels + "\")' href='javascript: void(0)'>删除</a>\
                    <a v-if='" + nodes[i].isChooseAll + "' ng-click='cancelAll(\"" + nextLevels + "\")' href='javascript: void(0)'>取消</a>\
                    <a v-if='" + !nodes[i].isChooseAll + "' ng-click='chooseAll(\"" + nextLevels + "\")' href='javascript: void(0)'>多选</a>\
                    <div class='children'>";
              recursion(nodes[i], nextLevels);
              rst += "</div>\
                    </li>\
                    </ul>";
            } else {
              var ui = 'fa-link';
              rst += "<ul class='role-list-item' style='padding-top:6px;'>\
                <li class='expanded'>\
                <input ng-checked='" + nodes[i].isChecked + "' ng-click='chooseChange(\"" + nextLevels + "\")' ng-if='" + n.isChooseAll + "' type='checkbox'>\
                <a class='a_inline' nodeid='" + nodes[i].id + "' href='javascript: void(0)' preImg='" + (n.preImg || '') + "'><i class='fa " + ui + "'></i>" + nodes[i].guideCate + "\
                <span class='node_id'> #" + nodes[i].id + "</span></a>\
                </li>\
                </ul>";
            }
          }
        }
      };

      recursion(n, levels);
      return rst += "</div>\
                    </li>\
                      </ul>";
    };

    console.log('rst', rst);
    var menuHtml = '<div class="menu_box">' + '<ul class="menu_list">' + '<li ng-click="addTree()">新增</li>' + '<li yes="delete()" msg="确定要删除此节点吗" my-confirm>删除</li>' + '<li>取消</li>' + '</ul>' + '</div>';
    var treeData = {
      guideCate: '标题1',
      id: 1,
      parentId: 1,
      nodes: [{
        guideCate: '标题2',
        id: 1,
        parentId: 1,
        nodes: [],
        isChooseAll: false
      }]
    };
    compile(treeData, 0);
    el.innerHTML = rst;
  },
  bind: function bind() {},
  update: function update() {},
  unbind: function unbind() {}
};
