result:
  // 用户通用浮层
.component-popup-wrap {
  position: absolute;
  top: 0px;
  display: none;
  cursor: pointer;
  &__content {
    border-radius: 6px;
    position: absolute;
    top: 0px;
    border-radius: 4px;
    box-shadow: 0 1px 2px #ddd;
    background: #fff;
    border: 1px solid #ddd;
    text-align: left;
    white-space: nowrap;
    font-size: 14px;
    line-height: 14px;
    padding-left: 2px;
    opacity: 0;
    > ul {
      margin: 0px;
      padding: 0px;
      li {
        list-style: none;
        text-align: center;
        padding: 8px 4px;
        display: inline-block;
        margin-right: 2px;
        vertical-align: middle;
        cursor: pointer;
        >a {
          display: inline-block;
          > i {
            color: #3c8dbc;
            vertical-align: bottom;
          }
        }
      }
    }
  }
}
