.page-wrap {
  margin-top: 15px;
  margin-left: 15px;
  .item {
    display: inline-block;
    border: 1px solid red;
    margin-right: 5px;
    height: 100px;
    vertical-align: middle;
    padding: 4px;
    .title {
      font-size: 18px;
      font-weight: bold;
      display: block;
      height: 36px;
    }
    .bouncing-loader {
      display: flex;
      justify-content: center;
      > div {
        width: 1rem;
        height: 1rem;
        margin: 3rem .2rem;
        background: #8385aa;
        border-radius: 50%;
        // alternate 动画轮流转向 infinite 播放次数无数次
        animation: bouncing-loader .6s infinite alternate;
      }
      .bouncing-loader > div:nth-child(2) {
        // 延迟加载
        animation-delay: .2s;
      }
      .bouncing-loader > div:nth-child(3) {
        animation-delay: .4s;
      }
    }
    // 按钮
    .button-border {
      text-align: center;
      .button {
        background-color: #c47135;
        border: none;
        color: #fff;
        outline: none;
        padding: 12px 40px 10px;
        position: relative;
        &::before, &::after {
          border: 0 solid transparent;
          transition: all .25s;
          content: '';
          height: 24px;
          position: absolute;
          width: 24px;
        }
        &::before {
          border-top: 2px solid #c47135;
          left: 0px;
          top: -5px;
        }
      }
    }
  }
}
@keyframes bouncing-loader {
  to {
    opacity: .1;
    transform: translate3d(0, -1rem, 0);
  }
}
