.header-component {
  height: 60px;
  width: 100%;
  line-height: 60px;
  position: fixed;
  max-width: 1000px;
  top: 0;
  background: #fff;
  border-bottom: 1px solid #ddd;
  box-shadow: 0 1px 1px rgba(0,0,0,.04);
  z-index: 9999;
  &__logo {
    font-size: 22px;
    a {
      text-decoration: none;
      color: #444;
    }
  }
  &__menu-list {
    margin-left: 50px;
    ul {
      list-style: none;
      padding: 0px;
      margin: 0px;
      li {
        display: inline-block;
        color: #000;
        font-size: 16px;
        padding: 0 20px;
        cursor: pointer;
        margin-right: 5px;
        &:hover {
          background: #3690cf;
          color: #fff;
        }
      }
      .selected {
        background: #3690cf;
        color: #fff;
      }
    }
  }
}
